'use strict';

(function (window, document, $, undefined) {

    $(document.body).append('\n        <div class="ts-alert">\n            <div class="ts-alert-content"></div>\n            <button class="ts-alert-btn">ok</button>\n        </div>\n    ');

    var $tsAlert = $('.ts-alert');
    var $tsAlertContent = $('.ts-alert-content');
    var $tsAlertBtn = $('.ts-alert-btn');
    var currentActiveElement = void 0;

    $tsAlertBtn.on('click', function (e) {
        $tsAlert.hide();
        currentActiveElement.focus();
    });

    window.tsAlert = function (msg) {
        currentActiveElement = document.activeElement;

        $tsAlertContent.empty().append(msg);
        $tsAlert.fadeIn(100);
        $tsAlertBtn.focus();
    };
})(window, document, jQuery);